def conwaysGoL():
    global conwayOnOff
    conwayNew = copy.deepcopy(conwayOnOff)
    columnLen = len(conwayOnOff)
    rowLen = len(conwayOnOff[0])
    for nthRow in range(columnLen):  # y
        for nthColumn in range(rowLen):  # x
            state = conwayOnOff[nthRow][nthColumn]
            neighbors = 0
            if conwayOnOff[nthRow - 1][nthColumn] == 1:
                neighbors += 1
            if conwayOnOff[nthRow - 1][(nthColumn + 1) % (rowLen)] == 1:
                neighbors += 1
            if conwayOnOff[nthRow][(nthColumn + 1) % (rowLen)] == 1:
                neighbors += 1
            if conwayOnOff[(nthRow + 1) % (columnLen)][(nthColumn + 1) % (rowLen)] == 1:
                neighbors += 1
            if conwayOnOff[(nthRow + 1) % (columnLen)][nthColumn] == 1:
                neighbors += 1
            if conwayOnOff[(nthRow + 1) % (columnLen)][nthColumn - 1] == 1:
                neighbors += 1
            if conwayOnOff[nthRow][nthColumn - 1] == 1:
                neighbors += 1
            if conwayOnOff[nthRow - 1][nthColumn - 1] == 1:
                neighbors += 1

            if state == 0:
                if neighbors == 3:
                    conwayNew[nthRow][nthColumn] = 1
            if state == 1:
                if neighbors < 2:
                    conwayNew[nthRow][nthColumn] = 0
                if neighbors > 3:
                    conwayNew[nthRow][nthColumn] = 0
    conwayOnOff = copy.deepcopy(conwayNew)
    return conwayOnOff


def copyW():
    global copyWorld
    conwayNew = copy.deepcopy(copyWorld)
    columnLen = len(copyWorld)
    rowLen = len(copyWorld[0])
    for nthRow in range(columnLen):
        for nthColumn in range(rowLen):
            neighbors = 0
            if copyWorld[nthRow - 1][nthColumn] == 1:
                neighbors += 1
            if copyWorld[nthRow - 1][(nthColumn + 1) % (rowLen)] == 1:
                neighbors += 1
            if copyWorld[nthRow][(nthColumn + 1) % (rowLen)] == 1:
                neighbors += 1
            if copyWorld[(nthRow + 1) % (columnLen)][(nthColumn + 1) % (rowLen)] == 1:
                neighbors += 1
            if copyWorld[(nthRow + 1) % (columnLen)][nthColumn] == 1:
                neighbors += 1
            if copyWorld[(nthRow + 1) % (columnLen)][nthColumn - 1] == 1:
                neighbors += 1
            if copyWorld[nthRow][nthColumn - 1] == 1:
                neighbors += 1
            if copyWorld[nthRow - 1][nthColumn - 1] == 1:
                neighbors += 1

            if neighbors % 2 == 0:
                conwayNew[nthRow][nthColumn] = 0
            else:
                conwayNew[nthRow][nthColumn] = 1

    copyWorld = copy.deepcopy(conwayNew)
    return copyWorld


def printArrBlocks(array):
    for i in range(len(array)):
        for j in range(len(array[0])):
            if array[i][j] == 1:
                print("██", end="")
            else:
                print("░░", end="")
        print("")
