def neighborsAt(y, x):
    yLen = len(conwayOnOff)
    xLen = len(conwayOnOff[0])
    neighbors = 0
    if conwayOnOff[y - 1][x] == 1:
        neighbors += 1
    if conwayOnOff[y - 1][(x + 1) % xLen] == 1:
        neighbors += 1
    if conwayOnOff[y][(x + 1) % xLen] == 1:
        neighbors += 1
    if conwayOnOff[(y + 1) % yLen][(x + 1) % xLen] == 1:
        neighbors += 1
    if conwayOnOff[(y + 1) % yLen][x] == 1:
        neighbors += 1
    if conwayOnOff[(y + 1) % yLen][x - 1] == 1:
        neighbors += 1
    if conwayOnOff[y][x - 1] == 1:
        neighbors += 1
    if conwayOnOff[y - 1][x - 1] == 1:
        neighbors += 1
    return neighbors


